example.code:
$("button").click(function () {
  $("div").each(function (index, domEle) {
    // domEle == this
    $(domEle).css("backgroundColor", "yellow"); 
    if ($(this).is("#stop")) {
      $("span").text("Stopped at div index #" + index);
      return false;
    }
  });
});

